  --gradient-stat-2: linear-gradient(135deg, oklch(0.55 0.20 200), oklch(0.58 0.18 215));
  --gradient-stat-3: linear-gradient(135deg, oklch(0.58 0.18 160), oklch(0.62 0.16 175));
  --gradient-stat-4: linear-gradient(135deg, oklch(0.58 0.20 320), oklch(0.62 0.18 335));
  --shadow-elegant: 0 4px 24px -4px oklch(0.48 0.22 264 / 0.18);
  --shadow-card:    0 1px 4px 0px oklch(0.18 0.02 264 / 0.06), 0 4px 16px -4px oklch(0.18 0.02 264 / 0.08);
  --shadow-sidebar: 4px 0 24px -4px oklch(0.18 0.02 264 / 0.18);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

* {
  box-sizing: border-box;
}

body {
  font-family: var(--font-body);
  background-color: var(--background);
  color: var(--foreground);
  -webkit-font-smoothing: antialiased;
}

/* === CUSTOM UTILITY CLASSES === */

.font-display {
  font-family: var(--font-display);
}

/* Stat cards with gradient backgrounds */
.stat-gradient-1 { background: var(--gradient-stat-1); }
.stat-gradient-2 { background: var(--gradient-stat-2); }
.stat-gradient-3 { background: var(--gradient-stat-3); }
.stat-gradient-4 { background: var(--gradient-stat-4); }

/* Hero gradient */
.hero-gradient { background: var(--gradient-hero); }

/* Elegant card style */
.card-elegant {
  background: var(--gradient-card);
  box-shadow: var(--shadow-card);
  border: 1px solid var(--border);
  border-radius: var(--radius-lg);
}

/* Badge variants */
.badge-paid {
  background: oklch(0.93 0.08 160);
  color: oklch(0.35 0.15 160);
  border: 1px solid oklch(0.85 0.10 160);
}
.badge-unpaid {
  background: oklch(0.95 0.05 25);
  color: oklch(0.45 0.18 25);
  border: 1px solid oklch(0.88 0.08 25);
}

/* Sidebar nav item active state */
.nav-item-active {
  background: var(--sidebar-accent);
  color: var(--sidebar-accent-foreground);
}

/* Table row hover */
.table-row-hover:hover {
  background: var(--accent);
}

/* Page header */
.page-header-gradient {
  background: linear-gradient(90deg, oklch(0.48 0.22 264 / 0.06), oklch(0.55 0.22 285 / 0.02));
  border-bottom: 1px solid var(--border);
}

